import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Tooltip } from 'react-tooltip';
import './Home.css';

function Home() {
  const [activeFeature, setActiveFeature] = useState(null);

  const features = [
    {
      id: 'microsoft365',
      icon: 'fas fa-cloud',
      title: 'Microsoft 365 Solutions',
      description: 'Streamline workflows and improve productivity with Microsoft\'s cloud ecosystem.',
      tooltip: 'Maximize your organization\'s use of cloud-based tools'
    },
    {
      id: 'cybersecurity',
      icon: 'fas fa-shield-alt',
      title: 'Cybersecurity',
      description: 'Safeguard your IT infrastructure against potential threats.',
      tooltip: 'Protect your business with advanced cybersecurity solutions'
    },
    {
      id: 'ai-content',
      icon: 'fas fa-brain',
      title: 'AI Writing & Image Generation',
      description: 'Produce high-quality, customized content for your business.',
      tooltip: 'Enhance your content creation with AI-driven tools'
    }
  ];

  return (
    <div className="home fade-in">
      <h1><i className="fas fa-rocket"></i> Welcome to Ahmed Sharfi's IT Solutions</h1>
      <p className="tagline">Where IT solutions meet innovation</p>
      <div className="intro">
        <p>As a passionate IT Solutions Architect, I bring a wealth of expertise in cloud computing, cybersecurity, AI-driven content creation, and enterprise solutions. From implementing robust IT infrastructures to leveraging AI for creative content, I provide tailored solutions designed to propel your business into the future.</p>
        <p>Let's work together to transform your technology, protect your data, and enhance your operations.</p>
      </div>
      <div className="features">
        {features.map((feature) => (
          <div
            key={feature.id}
            className={`feature ${activeFeature === feature.id ? 'active' : ''}`}
            data-tooltip-id={feature.id}
            data-tooltip-content={feature.tooltip}
            onMouseEnter={() => setActiveFeature(feature.id)}
            onMouseLeave={() => setActiveFeature(null)}
          >
            <i className={feature.icon}></i>
            <h2>{feature.title}</h2>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
      <Link to="/contact" className="cta-button">Get in Touch</Link>
      {features.map((feature) => (
        <Tooltip key={feature.id} id={feature.id} />
      ))}
    </div>
  );
}

export default Home;
