import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import * as THREE from 'three';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import Atropos from 'atropos/react';
import './Home.css';

function Home() {
  const mount = useRef(null);
  const rendererRef = useRef(null);
  const cursorRef = useRef(null);
  const textRef = useRef(null);

  useEffect(() => {
    if (!mount.current) return;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });

    rendererRef.current = renderer;

    renderer.setSize(400, 400);
    renderer.setClearColor(0x000000, 0);
    mount.current.appendChild(renderer.domElement);

    // Create terminal
    const terminalGeometry = new THREE.BoxGeometry(3, 2, 0.1);
    const terminalMaterial = new THREE.MeshPhongMaterial({ color: 0x2E073F, emissive: 0x2E073F, emissiveIntensity: 0.2 });
    const terminal = new THREE.Mesh(terminalGeometry, terminalMaterial);
    scene.add(terminal);

    // Create screen
    const screenGeometry = new THREE.PlaneGeometry(2.8, 1.8);
    const screenMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
    const screen = new THREE.Mesh(screenGeometry, screenMaterial);
    screen.position.z = 0.06;
    terminal.add(screen);

    // Add lights
    const ambientLight = new THREE.AmbientLight(0x404040);
    scene.add(ambientLight);

    const pointLight = new THREE.PointLight(0xffffff, 1, 100);
    pointLight.position.set(5, 5, 5);
    scene.add(pointLight);

    camera.position.z = 3;

    // Load font
    const loader = new FontLoader();
    loader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', function(font) {
      // Create cursor
      const cursorGeometry = new TextGeometry('|', {
        font: font,
        size: 0.15,
        height: 0.02,
      });
      const cursorMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00 });
      cursorRef.current = new THREE.Mesh(cursorGeometry, cursorMaterial);
      cursorRef.current.position.set(-1.3, 0.7, 0.07);
      screen.add(cursorRef.current);

      // Create scrolling text
      const textGeometry = new TextGeometry('', {
        font: font,
        size: 0.08,
        height: 0.01,
      });
      const textMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00 });
      textRef.current = new THREE.Mesh(textGeometry, textMaterial);
      textRef.current.position.set(-1.3, 0.7, 0.07);
      screen.add(textRef.current);
    });

    // Animation
    let cursorVisible = true;
    let textIndex = 0;
    const codeLines = [
      "function optimizeIT() {",
      "  implementCloud();",
      "  enhanceSecurity();",
      "  integrateAI();",
      "  return success;",
      "}",
      "",
      "optimizeIT();"
    ];
    let currentLine = "";

    const animate = () => {
      requestAnimationFrame(animate);

      terminal.rotation.x += 0.005;
      terminal.rotation.y += 0.005;

      // Blink cursor
      if (cursorRef.current) {
        cursorVisible = !cursorVisible;
        cursorRef.current.visible = cursorVisible;
      }

      // Scroll text
      if (textRef.current && textIndex < codeLines.join('\n').length) {
        if (currentLine.length === 0) {
          currentLine = codeLines[Math.floor(textIndex / 20)] + '\n';
        }
        textRef.current.geometry.dispose();
        textRef.current.geometry = new TextGeometry(currentLine.substring(0, textIndex % 20 + 1), {
          font: textRef.current.geometry.parameters.font,
          size: 0.08,
          height: 0.01,
        });
        textIndex++;
        if (textIndex % 20 === 0) {
          currentLine = "";
        }
      } else if (textRef.current) {
        textIndex = 0;
      }

      renderer.render(scene, camera);
    };

    animate();

    const handleResize = () => {
      if (!mount.current || !rendererRef.current) return;

      const width = mount.current.clientWidth;
      const height = mount.current.clientHeight;
      rendererRef.current.setSize(width, height);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (mount.current && rendererRef.current) {
        mount.current.removeChild(rendererRef.current.domElement);
      }
      if (rendererRef.current) {
        rendererRef.current.dispose();
      }
    };
  }, []);

  const services = [
    { icon: 'fas fa-cloud', name: 'Cloud Solutions', description: 'Optimize your business with cutting-edge cloud technologies.' },
    { icon: 'fas fa-shield-alt', name: 'Cybersecurity', description: 'Protect your digital assets with robust security measures.' },
    { icon: 'fas fa-robot', name: 'AI Integration', description: 'Leverage the power of AI to drive innovation and efficiency.' },
    { icon: 'fas fa-cogs', name: 'IT Consulting', description: 'Expert advice to align your IT strategy with business goals.' }
  ];

  return (
    <div className="home">
      <section className="hero">
        <div className="hero-content">
          <h1 className="fade-in-up">Innovative IT Solutions</h1>
          <p className="fade-in-up">Empowering businesses with cutting-edge technology</p>
          <Link to="/contact" className="btn fade-in-up">Get Started</Link>
        </div>
        <div className="hero-image fade-in-up">
          <div ref={mount} className="terminal-visualization"></div>
        </div>
      </section>

      <section className="services">
        <h2 className="section-title fade-in-up">What I Do</h2>
        <div className="service-grid">
          {services.map((service, index) => (
            <Atropos
              key={index}
              className="service-card-wrapper"
              highlight={false}
              shadow={false}
            >
              <div className="service-card fade-in-up">
                <i className={`${service.icon} service-icon`} data-atropos-offset="5"></i>
                <h3 data-atropos-offset="4">{service.name}</h3>
                <p data-atropos-offset="3">{service.description}</p>
              </div>
            </Atropos>
          ))}
        </div>
      </section>

      <section className="about-snippet">
        <div className="container">
          <h2 className="section-title fade-in-up">Why Choose Us</h2>
          <div className="about-grid">
            <div className="about-item fade-in-up">
              <i className="fas fa-rocket"></i>
              <h3>Innovative Solutions</h3>
              <p>We leverage the latest technologies to drive your business forward.</p>
            </div>
            <div className="about-item fade-in-up">
              <i className="fas fa-shield-alt"></i>
              <h3>Robust Security</h3>
              <p>Your data's safety is our top priority with state-of-the-art security measures.</p>
            </div>
            <div className="about-item fade-in-up">
              <i className="fas fa-users"></i>
              <h3>Expert Team</h3>
              <p>Our seasoned professionals bring years of industry experience to your projects.</p>
            </div>
          </div>
        </div>
      </section>

      <section className="cta">
        <div className="container">
          <h2 className="fade-in-up">Ready to Transform Your IT Infrastructure?</h2>
          <p className="fade-in-up">Let's discuss how we can elevate your business with our IT solutions.</p>
          <Link to="/contact" className="btn fade-in-up">Contact Us</Link>
        </div>
      </section>
    </div>
  );
}

export default Home;
